    private void btntotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btntotalActionPerformed
        //Integer.perseInt --> merubah txt menjadi integer
        int harga = Integer.parseInt(txtharga.getText()); 
        int jumlah = Integer.parseInt(txtjumlah.getText());
        double diskon = 0;
        
        int tb = harga * jumlah;
        txttotalbeli.setText(Integer.toString(tb));
        //Integer.toString --> merubah int menjadi txt
        
        //DISKON
        if(tb>100000){
           diskon = 0.1 *tb;
        }
        txtdiskon.setText(Double.toString(diskon));
        double tbayar = tb - diskon;
        txttotalbayar.setText(Double.toString(tbayar));
    }//GEN-LAST:event_btntotalActionPerformed

