    private void btnkembalianActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnkembalianActionPerformed
        double tbayar = Double.parseDouble(txttotalbayar.getText());
        int uang = Integer.parseInt(txtpembayaran.getText());
        
        if(uang < tbayar){
            JOptionPane.showConfirmDialog(this,"Uang anda kurang","Pesan",JOptionPane.INFORMATION_MESSAGE);
        }else{
            double kembalian = uang - tbayar;
            txtkembalian.setText(Double.toString(kembalian));
        }
        
    }//GEN-LAST:event_btnkembalianActionPerformed

    private void btnresetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnresetActionPerformed
        cbnama.setSelectedIndex(0);
        txtharga.setText("");
        txtjumlah.setText("");
        txtkembalian.setText("");
        txtdiskon.setText("");
        txttotalbayar.setText("");
        txtpembayaran.setText("");
        txttotalbayar.setText("");
        txttotalbeli.setText("");
    }//GEN-LAST:event_btnresetActionPerformed

    private void btnexitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnexitActionPerformed
        int pil = JOptionPane.showConfirmDialog(this,"Apakah yakin ingin keluar?","Peringatan",JOptionPane.YES_NO_OPTION);
        
        if(pil==JOptionPane.YES_OPTION){
            System.exit(0);
        }
    }//GEN-LAST:event_btnexitActionPerformed

